import { EFieldType, IField } from '../../types/form/field';
import { fieldTypeMap } from './dynamic-form.variables';
import React from 'react';
import { titleCase } from 'voca';
import { Box, FormField, TextInput } from 'grommet';

interface IFormProps {
  fields: IField[];
  direction?: 'row' | 'column';
}

export const DynamicForm = ({ fields }: IFormProps) => {
  return (
    <Box>
      {fields?.map((field) => {
        const Input = fieldTypeMap.get(type) || TextInput;
        const selectConfig =
          field.type === EFieldType.SELECT ||
          field.type == EFieldType.RADIO_GROUP
            ? {
                labelKey: 'value',
                valueKey: 'label',
              }
            : {};

        return (
          <FormField label={titleCase(field.name)}>
            <Input
              {...field}
              type={field.type === EFieldType.NUMBER ? 'number' : 'text'}
              {...selectConfig}
            />
          </FormField>
        );
      })}
    </Box>
  );
};
